import React, { useState, useEffect } from "react";
import {
  Card,
  Typography,
  Button,
  Row,
  Col,
  Tag,
  Divider,
  Modal,
  Spin,
  Empty,
  message,
  Image,
} from "antd";
import {
  ClockCircleOutlined,
  EnvironmentOutlined,
  CalendarOutlined,
  InfoCircleOutlined,
} from "@ant-design/icons";
import bookingService from "../../../services/booking/bookingService";

const { Title, Text } = Typography;

const MyTicket = () => {
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTicketHistory = async () => {
      try {
        setLoading(true);
        const response = await bookingService.getUserTicketHistory();
        setTickets(response.result || []);
      } catch (error) {
        console.error("Failed to fetch ticket history:", error);
        message.error("Không thể tải lịch sử vé. Vui lòng thử lại sau.");
      } finally {
        setLoading(false);
      }
    };

    fetchTicketHistory();
  }, []);

  const showTicketDetails = (ticket) => {
    setSelectedTicket(ticket);
    setIsModalVisible(true);
  };

  const handleCloseModal = () => {
    setIsModalVisible(false);
    setSelectedTicket(null);
  };

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const date = new Date(dateString);
    return date.toLocaleDateString("vi-VN");
  };

  const formatTime = (dateString) => {
    if (!dateString) return "";
    const date = new Date(dateString);
    return date.toLocaleTimeString("vi-VN", {
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const formatDuration = (minutes) => {
    if (!minutes) return "";
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  return (
    <div style={{ padding: "24px" }}>
      <Title level={2}>Lịch sử vé đã mua</Title>
      <Divider />

      {loading ? (
        <div style={{ textAlign: "center", padding: "40px 0" }}>
          <Spin size="large" />
        </div>
      ) : tickets.length === 0 ? (
        <Empty description="Bạn chưa có vé nào" />
      ) : (
        tickets.map((ticket, index) => (
          <Card
            key={index}
            style={{ marginBottom: "16px", borderRadius: "8px" }}
            hoverable
            onClick={() => showTicketDetails(ticket)}
          >
            <Row gutter={[16, 16]}>
              <Col span={4}>
                <Image
                  src={ticket.moviePosterUrl}
                  alt={ticket.movieTitle}
                  style={{ width: "100%", borderRadius: "4px" }}
                  fallback="data:image/png;base64,"
                />
              </Col>
              <Col span={12}>
                <Title level={4}>{ticket.movieTitle}</Title>
                <div style={{ marginBottom: "8px" }}>
                  <EnvironmentOutlined /> {ticket.cinemaName} -{" "}
                  {ticket.roomName} ({ticket.roomType})
                </div>
                <div style={{ marginBottom: "8px" }}>
                  <CalendarOutlined /> {formatDate(ticket.showtimeStart)}
                </div>
                <div style={{ marginBottom: "8px" }}>
                  <ClockCircleOutlined /> {formatTime(ticket.showtimeStart)} -{" "}
                  {formatTime(ticket.showtimeEnd)} (
                  {formatDuration(ticket.movieDuration)})
                </div>
                <div>
                  <Text strong>Ghế: </Text>
                  {ticket.seatCodes ? ticket.seatCodes.join(", ") : ""}
                </div>
              </Col>
              <Col span={8} style={{ textAlign: "right" }}>
                <Tag
                  color={ticket.paymentStatus === "SUCCESS" ? "green" : "blue"}
                  style={{ marginBottom: "16px" }}
                >
                  {ticket.paymentStatus === "SUCCESS"
                    ? "Đã Thanh Toán"
                    : "Đang Xử Lý"}
                </Tag>
                <div style={{ marginBottom: "16px" }}>
                  <Text strong>Mã vé: </Text>
                  <br />
                  <Text copyable>{ticket.ticketCode}</Text>
                </div>
                <Button type="primary" icon={<InfoCircleOutlined />}>
                  Xem chi tiết
                </Button>
              </Col>
            </Row>
          </Card>
        ))
      )}

      <Modal
        title="Chi tiết vé"
        open={isModalVisible}
        onCancel={handleCloseModal}
        footer={[
          <Button key="close" onClick={handleCloseModal}>
            Đóng
          </Button>,
        ]}
        width={600}
      >
        {selectedTicket && (
          <div>
            <Row gutter={[16, 16]}>
              <Col span={6}>
                <Image
                  src={selectedTicket.moviePosterUrl}
                  alt={selectedTicket.movieTitle}
                  style={{ width: "100%", borderRadius: "4px" }}
                  fallback="data:image/png;base64,"
                />
              </Col>
              <Col span={18}>
                <Title level={4}>{selectedTicket.movieTitle}</Title>
                <Text type="secondary">
                  Thời lượng: {formatDuration(selectedTicket.movieDuration)}
                </Text>
                <Divider />
                <Row gutter={[16, 16]}>
                  <Col span={12}>
                    <Text strong>Mã vé:</Text>
                    <br />
                    <Text copyable>{selectedTicket.ticketCode}</Text>
                  </Col>
                  <Col span={12}>
                    <Text strong>Ngày đặt:</Text>
                    <br />
                    <Text>
                      {formatDate(selectedTicket.bookingTime)}{" "}
                      {formatTime(selectedTicket.bookingTime)}
                    </Text>
                  </Col>
                </Row>
                <Divider />
                <Row gutter={[16, 16]}>
                  <Col span={12}>
                    <Text strong>Rạp chiếu:</Text>
                    <br />
                    <Text>{selectedTicket.cinemaName}</Text>
                  </Col>
                  <Col span={12}>
                    <Text strong>Phòng chiếu:</Text>
                    <br />
                    <Text>
                      {selectedTicket.roomName} ({selectedTicket.roomType})
                    </Text>
                  </Col>
                </Row>
                <Divider />
                <Row gutter={[16, 16]}>
                  <Col span={12}>
                    <Text strong>Ngày chiếu:</Text>
                    <br />
                    <Text>{formatDate(selectedTicket.showtimeStart)}</Text>
                  </Col>
                  <Col span={12}>
                    <Text strong>Giờ chiếu:</Text>
                    <br />
                    <Text>
                      {formatTime(selectedTicket.showtimeStart)} -{" "}
                      {formatTime(selectedTicket.showtimeEnd)}
                    </Text>
                  </Col>
                </Row>
                <Divider />
                <Row gutter={[16, 16]}>
                  <Col span={12}>
                    <Text strong>Ghế:</Text>
                    <br />
                    <Text>
                      {selectedTicket.seatCodes
                        ? selectedTicket.seatCodes.join(", ")
                        : ""}
                    </Text>
                  </Col>
                  <Col span={12}>
                    <Text strong>Giá vé:</Text>
                    <br />
                    <Text>
                      {selectedTicket.totalPrice
                        ? `${selectedTicket.totalPrice.toLocaleString()}đ`
                        : ""}
                    </Text>
                  </Col>
                </Row>
              </Col>
            </Row>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default MyTicket;
